import React, { useState, useEffect } from 'react';
import GanttChart from './components/GanttChart';
import Metrics from './components/Metrics';
import './App.css';

const defaultConfig = `# Process configuration file
# Format: name, arrival_time, burst_time, priority
# Lower number = higher priority
# Lines starting with # and blank lines are ignored.
P1, 0, 8, 3
P2, 1, 4, 1
P3, 2, 9, 4
P4, 3, 5, 2
P5, 4, 2, 5
`;

function App() {
    const [algorithms, setAlgorithms] = useState([]);
    const [selectedAlgorithm, setSelectedAlgorithm] = useState('');
    const [processConfig, setProcessConfig] = useState(defaultConfig);
    const [quantum, setQuantum] = useState(2);
    const [simulationResult, setSimulationResult] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [isInitialLoad, setIsInitialLoad] = useState(true);

    useEffect(() => {
        fetch('http://localhost:3001/api/algorithms')
            .then(res => res.json())
            .then(data => {
                // We still sort the data so the dropdown list in the UI is nicely organized.
                const sortedData = data.sort(); 
                setAlgorithms(sortedData);

                // --- NEW LOGIC TO SET DEFAULT SELECTION ---
                // We explicitly check if 'FIFO' exists in the list.
                if (sortedData.includes('FIFO')) {
                    setSelectedAlgorithm('FIFO');
                } else if (sortedData.length > 0) {
                    // If not, fall back to the first one in the sorted list.
                    setSelectedAlgorithm(sortedData[0]);
                }
            })
            .catch(() => setError("Could not connect to backend. Is it running?"));
    }, []);

    const runSimulation = (algoToRun, configToUse, quantumToUse) => {
        setIsLoading(true);
        setSimulationResult(null);
        setError(null);

        let payload = {
            algorithm: algoToRun,
            processes: configToUse,
        };
        if (algoToRun === 'RR') {
            payload.quantum = quantumToUse;
        }

        fetch('http://localhost:3001/api/run', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
        })
        .then(res => res.ok ? res.json() : res.json().then(err => Promise.reject(err)))
        .then(data => {
            setSimulationResult(data);
            setIsLoading(false);
        })
        .catch(err => {
            setError(err.error || 'Simulation failed. Check console and process format.');
            setIsLoading(false);
        });
    };

    // --- UPDATED useEffect for Autorun by name ---
    useEffect(() => {
        // We only want to autorun if:
        // 1. It's the initial page load.
        // 2. The algorithm list has been successfully fetched.
        if (isInitialLoad && algorithms.length > 0) {
            // Define our desired default algorithm explicitly.
            const defaultAlgorithm = "FIFO";

            // Check if our desired default exists in the list from the backend.
            if (algorithms.includes(defaultAlgorithm)) {
                // If it exists, run it!
                runSimulation(defaultAlgorithm, defaultConfig, quantum);
            } else {
                // As a safe fallback, if "FIFO" is not found, just run the first one available.
                runSimulation(algorithms[0], defaultConfig, quantum);
            }
            // Prevent this effect from ever running automatically again.
            setIsInitialLoad(false);
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [algorithms, isInitialLoad]); // We only want this to run when these specific states change.

    return (
        <div className="container">
            <header>
                <div className="logo">
                    <svg width="32" height="32" viewBox="0 0 24 24" fill="#7d8590" xmlns="http://www.w3.org/2000/svg"><path d="M4 6a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v2a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2V6zm0 10a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v2a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2v-2zm10-5H6a2 2 0 0 0-2 2v2a2 2 0 0 0 2 2h8a2 2 0 0 0 2-2v-2a2 2 0 0 0-2-2z" fill="#39c5bb"/></svg>
                    <h1>OS Process Scheduler Simulator</h1>
                </div>
            </header>
            <main className="main-grid">
                <div className="controls-panel">
                    <div className="card">
                        <div className="form-group">
                            <label htmlFor="algo-select">Scheduling Algorithm</label>
                            <select id="algo-select" value={selectedAlgorithm} onChange={e => setSelectedAlgorithm(e.target.value)}>
                                {algorithms.map(algo => <option key={algo} value={algo}>{algo}</option>)}
                            </select>
                        </div>

                        {selectedAlgorithm === 'RR' && (
                            <div className="form-group">
                                <label htmlFor="quantum-input">Quantum Time Slice</label>
                                <input
                                    type="number"
                                    id="quantum-input"
                                    value={quantum}
                                    onChange={e => setQuantum(Number(e.target.value))}
                                    min="1"
                                />
                            </div>
                        )}

                        <button 
                            className="btn-run" 
                            onClick={() => runSimulation(selectedAlgorithm, processConfig, quantum)} 
                            disabled={isLoading || !selectedAlgorithm}>
                            {isLoading ? 'Simulating...' : 'Run Simulation'}
                        </button>
                    </div>
                    <div className="card">
                        <label htmlFor="config-textarea">Process Configuration</label>
                        <textarea 
                            id="config-textarea" 
                            value={processConfig} 
                            onChange={e => setProcessConfig(e.target.value)} 
                        />
                    </div>
                </div>
                <div className="results-panel">
                    {error && <div className="card error-panel">Error: {error}</div>}
                    {!simulationResult && !isLoading && !error && (
                        <div className="card placeholder">
                            <div>
                                <h2>Welcome!</h2>
                                <p>Loading initial simulation...</p>
                            </div>
                        </div>
                    )}
                    {isLoading && <div className="card placeholder">Simulating...</div>}
                    {simulationResult && (
                        <>
                            <GanttChart data={simulationResult} />
                            <Metrics data={simulationResult} />
                        </>
                    )}
                </div>
            </main>
        </div>
    );
}

export default App;
