import React, { useState, useEffect } from 'react';
import GanttChart from './GanttChart';
import './App.css';

function App() {
  // State to hold our data
  const [algorithms, setAlgorithms] = useState([]);
  const [simulationResult, setSimulationResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // This function runs once when the component is first rendered
  useEffect(() => {
    // Fetch the list of available algorithms from our backend server
    fetch('http://localhost:3001/api/algorithms')
      .then(res => res.json())
      .then(data => setAlgorithms(data))
      .catch(() => setError("Could not connect to the backend server. Is it running?"));
  }, []);

  // This function is called when a user clicks a button
  const runSimulation = (algoName) => {
    setIsLoading(true);
    setSimulationResult(null); // Clear previous results
    setError(null);

    fetch(`http://localhost:3001/api/run/${algoName}`, { method: 'POST' })
      .then(res => {
        if (!res.ok) throw new Error(`Simulation failed on the server.`);
        return res.json();
      })
      .then(data => {
        setSimulationResult(data);
        setIsLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setIsLoading(false);
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Visual Process Scheduler</h1>
        <p>Select an algorithm to run the simulation.</p>
      </header>

      <div className="ControlPanel">
        <h3>Available Algorithms</h3>
        <div className="Button-Group">
          {algorithms.length > 0 ? (
            algorithms.map(algo => (
              <button key={algo} onClick={() => runSimulation(algo)} disabled={isLoading}>
                {algo}
              </button>
            ))
          ) : (
            <p>Loading algorithms...</p>
          )}
        </div>
      </div>

      <main className="Content">
        {isLoading && <div className="loader">Running Simulation...</div>}
        {error && <div className="error-panel">Error: {error}</div>}
        {simulationResult && (
          <GanttChart data={simulationResult} />
        )}
      </main>
    </div>
  );
}

export default App;
