import React, { useState, useEffect } from 'react';
import GanttChart from './components/GanttChart';
import Metrics from './components/Metrics';
import './App.css';

const defaultConfig = `# Process configuration file
# Format: name, arrival_time, burst_time, priority
# Lower number = higher priority
# Lines starting with # and blank lines are ignored.
P1, 0, 8, 3
P2, 1, 4, 1
P3, 2, 9, 4
P4, 3, 5, 2
P5, 4, 2, 5
`;

function App() {
    const [algorithms, setAlgorithms] = useState([]);
    const [selectedAlgorithm, setSelectedAlgorithm] = useState('');
    const [processConfig, setProcessConfig] = useState(defaultConfig);
    // NEW state for the quantum value
    const [quantum, setQuantum] = useState(2); 
    const [simulationResult, setSimulationResult] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch('http://localhost:3001/api/algorithms')
            .then(res => res.json())
            .then(data => {
                setAlgorithms(data);
                if (data.length > 0) setSelectedAlgorithm(data[0]);
            })
            .catch(() => setError("Could not connect to backend. Is it running?"));
    }, []);

    const runSimulation = () => {
        setIsLoading(true);
        setSimulationResult(null);
        setError(null);

        // --- NEW LOGIC to build the request body ---
        let payload = {
            algorithm: selectedAlgorithm,
            processes: processConfig,
        };

        // If the selected algorithm is RR, add the quantum to the payload
        if (selectedAlgorithm === 'RR') {
            payload.quantum = quantum;
        }

        fetch('http://localhost:3001/api/run', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload) // Send the constructed payload
        })
        .then(res => res.ok ? res.json() : res.json().then(err => Promise.reject(err)))
        .then(data => {
            setSimulationResult(data);
            setIsLoading(false);
        })
        .catch(err => {
            setError(err.error || 'Simulation failed');
            setIsLoading(false);
        });
    };

    return (
        <div className="container">
            <header>{/* ... header is the same ... */}</header>
            <main className="main-grid">
                <div className="controls-panel">
                    <div className="card">
                        <div className="form-group">
                            <label htmlFor="algo-select">Scheduling Algorithm</label>
                            <select id="algo-select" value={selectedAlgorithm} onChange={e => setSelectedAlgorithm(e.target.value)}>
                                {algorithms.map(algo => <option key={algo} value={algo}>{algo}</option>)}
                            </select>
                        </div>

                        {/* --- NEW JSX: CONDITIONAL QUANTUM INPUT --- */}
                        {selectedAlgorithm === 'RR' && (
                            <div className="form-group">
                                <label htmlFor="quantum-input">Quantum Time Slice</label>
                                <input
                                    type="number"
                                    id="quantum-input"
                                    value={quantum}
                                    onChange={e => setQuantum(Number(e.target.value))}
                                    min="1"
                                />
                            </div>
                        )}

                        <button className="btn-run" onClick={runSimulation} disabled={isLoading || !selectedAlgorithm}>
                            {isLoading ? 'Simulating...' : 'Run Simulation'}
                        </button>
                        {/* The EXPLAIN BUTTON IS REMOVED */}
                    </div>
                    <div className="card">{/* ... textarea is the same ... */}</div>
                </div>
                <div className="results-panel">{/* ... results panel is the same ... */}</div>
            </main>
        </div>
    );
}
export default App;
