import React, { useState, useEffect } from 'react';
import GanttChart from './components/GanttChart';
import Metrics from './components/Metrics';
import './App.css';

// The hardcoded defaultConfig is now REMOVED.

function App() {
    const [algorithms, setAlgorithms] = useState([]);
    const [selectedAlgorithm, setSelectedAlgorithm] = useState('');
    // Initialize processConfig as an empty string; it will be populated by the fetch.
    const [processConfig, setProcessConfig] = useState(''); 
    const [quantum, setQuantum] = useState(2);
    const [simulationResult, setSimulationResult] = useState(null);
    const [isLoading, setIsLoading] = useState(true); // App starts in a loading state.
    const [error, setError] = useState(null);
    const [isInitialLoad, setIsInitialLoad] = useState(true);

    // --- UPDATED useEffect: Fetches both algorithms and the params.txt file ---
    useEffect(() => {
        Promise.all([
            fetch('http://localhost:3001/api/algorithms'), // API call to your C server
            fetch('/params.txt')                         // Local fetch from the /public folder
        ])
        .then(([algosRes, paramsRes]) => {
            if (!algosRes.ok) throw new Error("Failed to fetch algorithms from backend.");
            if (!paramsRes.ok) throw new Error("Failed to fetch params.txt. Make sure it's in the React app's 'public' folder.");
            return Promise.all([algosRes.json(), paramsRes.text()]);
        })
        .then(([algosData, paramsText]) => {
            const sortedData = algosData.sort(); 
            setAlgorithms(sortedData);
            setProcessConfig(paramsText); // This populates the textarea!

            // Set the default selected algorithm to FIFO if available
            if (sortedData.includes('FIFO')) {
                setSelectedAlgorithm('FIFO');
            } else if (sortedData.length > 0) {
                setSelectedAlgorithm(sortedData[0]);
            }
        })
        .catch((err) => {
            console.error("Initialization Error:", err);
            setError(err.message || "Could not load initial data. Check that the C backend is running.");
            setIsLoading(false); // Stop loading on error
        });
    }, []); // Empty array [] ensures this runs only once when the app mounts.

    const runSimulation = (algoToRun, configToUse, quantumToUse) => {
        // We set isLoading here so the user sees feedback on manual runs too.
        setIsLoading(true); 
        setSimulationResult(null);
        setError(null);

        let payload = {
            algorithm: algoToRun,
            processes: configToUse,
        };
        if (algoToRun === 'RR') {
            payload.quantum = quantumToUse;
        }

        fetch('http://localhost:3001/api/run', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
        })
        .then(res => res.ok ? res.json() : res.json().then(err => Promise.reject(err)))
        .then(data => {
            setSimulationResult(data);
            setIsLoading(false);
        })
        .catch(err => {
            setError(err.error || 'Simulation failed. Check console and process format.');
            setIsLoading(false);
        });
    };

    // This autorun effect now waits for processConfig to be loaded from the file.
    useEffect(() => {
        // Autorun only if it's the initial load AND both data sources are ready.
        if (isInitialLoad && algorithms.length > 0 && processConfig !== '') {
            const defaultAlgorithm = "FIFO";
            const initialAlgo = algorithms.includes(defaultAlgorithm) ? defaultAlgorithm : algorithms[0];

            // Run the simulation with the data loaded from params.txt.
            runSimulation(initialAlgo, processConfig, quantum); 
            setIsInitialLoad(false);
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [algorithms, processConfig, isInitialLoad]);

    return (
        <div className="container">
            <header>
                <div className="logo">
                    <svg width="32" height="32" viewBox="0 0 24 24" fill="#7d8590" xmlns="http://www.w3.org/2000/svg"><path d="M4 6a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v2a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2V6zm0 10a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v2a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2v-2zm10-5H6a2 2 0 0 0-2 2v2a2 2 0 0 0 2 2h8a2 2 0 0 0 2-2v-2a2 2 0 0 0-2-2z" fill="#39c5bb"/></svg>
                    <h1>OS Process Scheduler Simulator</h1>
                </div>
            </header>
            <main className="main-grid">
                <div className="controls-panel">
                    <div className="card">
                        <div className="form-group">
                            <label htmlFor="algo-select">Scheduling Algorithm</label>
                            <select id="algo-select" value={selectedAlgorithm} onChange={e => setSelectedAlgorithm(e.target.value)}>
                                {algorithms.map(algo => <option key={algo} value={algo}>{algo}</option>)}
                            </select>
                        </div>

                        {selectedAlgorithm === 'RR' && (
                            <div className="form-group">
                                <label htmlFor="quantum-input">Quantum Time Slice</label>
                                <input
                                    type="number"
                                    id="quantum-input"
                                    value={quantum}
                                    onChange={e => setQuantum(Number(e.target.value))}
                                    min="1"
                                />
                            </div>
                        )}

                        <button 
                            className="btn-run" 
                            onClick={() => runSimulation(selectedAlgorithm, processConfig, quantum)} 
                            disabled={isLoading || !selectedAlgorithm}>
                            {/* The text changes depending on if it's the initial auto-run or a manual one */}
                            {isLoading ? 'Simulating...' : 'Run Simulation'}
                        </button>
                    </div>
                    <div className="card">
                        <label htmlFor="config-textarea">Process Configuration</label>
                        <textarea 
                            id="config-textarea" 
                            value={processConfig} 
                            onChange={e => setProcessConfig(e.target.value)} 
                        />
                    </div>
                </div>
                <div className="results-panel">
                    {error && <div className="card error-panel">Error: {error}</div>}
                    {/* The text now reflects that data is loading from a file */}
                    {!simulationResult && isLoading && (
                        <div className="card placeholder">
                           <div>
                                <h2>{isInitialLoad ? "Loading Initial Data..." : "Simulating..."}</h2>
                           </div>
                        </div>
                    )}
                    {!simulationResult && !isLoading && !error && (
                        <div className="card placeholder">
                            <div>
                                <h2>Welcome!</h2>
                                <p>All data loaded. Ready to run a new simulation.</p>
                            </div>
                        </div>
                    )}
                    {simulationResult && (
                        <>
                            <GanttChart data={simulationResult} />
                            <Metrics data={simulationResult} />
                        </>
                    )}
                </div>
            </main>
        </div>
    );
}

export default App;
