import React from 'react';
import './GanttChart.css';

// This component receives the simulation data as a "prop"
const GanttChart = ({ data }) => {
  if (!data || !data.ganttChart) return null;

  // Find the total simulation time to calculate the scale
  const totalTime = data.ganttChart.reduce((max, block) => Math.max(max, block.end), 0);
  const scale = 700 / totalTime; // 700 pixels for the total chart width

  return (
    <div className="gantt-container">
      <h2>Results for: {data.algorithm}</h2>
      <div className="gantt-chart-wrapper">
        <div className="gantt-chart">
          {data.ganttChart.map((block, index) => (
            <div
              key={block.pid + index}
              className="gantt-block"
              style={{
                left: `${block.start * scale}px`,
                width: `${(block.end - block.start) * scale}px`,
              }}
              title={`Process ${block.pid} (${block.start} to ${block.end})`}
            >
              <span className="pid-label">{block.pid}</span>
            </div>
          ))}
        </div>
        <div className="timeline">
          {[...Array(totalTime + 1).keys()].map(time => (
             <div key={time} className="time-marker" style={{ left: `${time * scale}px` }}>
               <span>{time}</span>
             </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default GanttChart;
