import React, { useState, useEffect } from 'react';
import GanttChart from './components/GanttChart';
import Metrics from './components/Metrics';
import './App.css';

const defaultConfig = `# Process configuration file
# Format: name, arrival_time, burst_time, priority
# Lower number = higher priority
# Lines starting with # and blank lines are ignored.
P1, 0, 8, 3
P2, 1, 4, 1
P3, 2, 9, 4
P4, 3, 5, 2
P5, 4, 2, 5
`;

function App() {
    const [algorithms, setAlgorithms] = useState([]);
    const [selectedAlgorithm, setSelectedAlgorithm] = useState('');
    const [processConfig, setProcessConfig] = useState(defaultConfig);
    const [quantum, setQuantum] = useState(2);
    const [simulationResult, setSimulationResult] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    // NEW state to prevent the initial auto-run from happening more than once
    const [initialLoad, setInitialLoad] = useState(true); 

    // This effect is unchanged: it fetches the list of algorithms on mount.
    useEffect(() => {
        fetch('http://localhost:3001/api/algorithms')
            .then(res => res.json())
            .then(data => {
                setAlgorithms(data);
                if (data.length > 0) {
                    setSelectedAlgorithm(data[0]); // Set the default selected algorithm
                }
            })
            .catch(() => setError("Could not connect to backend. Is it running?"));
    }, []); // Empty dependency array means this runs only once after the initial render.

    // This is the function to run the simulation (unchanged).
    const runSimulation = (algoName, config, quant) => {
        setIsLoading(true);
        setSimulationResult(null);
        setError(null);

        let payload = {
            algorithm: algoName,
            processes: config,
        };
        if (algoName === 'RR') {
            payload.quantum = quant;
        }

        fetch('http://localhost:3001/api/run', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
        })
        .then(res => res.ok ? res.json() : res.json().then(err => Promise.reject(err)))
        .then(data => {
            setSimulationResult(data);
            setIsLoading(false);
        })
        .catch(err => {
            setError(err.error || 'Simulation failed. Check console and process format.');
            setIsLoading(false);
        });
    };

    // --- NEW `useEffect` TO HANDLE AUTO-RUN ---
    // This effect will run whenever the `algorithms` state changes.
    useEffect(() => {
        // We only want to auto-run if:
        // 1. This is the very first time we've loaded algorithms (initialLoad is true).
        // 2. The algorithms array is now populated (algorithms.length > 0).
        if (initialLoad && algorithms.length > 0) {
            // Run the simulation with the first algorithm in the list.
            runSimulation(algorithms[0], defaultConfig, quantum);
            // Prevent this effect from ever running again automatically.
            setInitialLoad(false); 
        }
    }, [algorithms, initialLoad]); // The dependency array: this code re-runs if `algorithms` or `initialLoad` changes.


    return (
        <div className="container">
            <header>{/*... a-t-on besoin de changer ici ...*/}</header>
            <main className="main-grid">
                <div className="controls-panel">
                    <div className="card">
                        <div className="form-group">
                            <label htmlFor="algo-select">Scheduling Algorithm</label>
                            <select id="algo-select" value={selectedAlgorithm} onChange={e => setSelectedAlgorithm(e.target.value)}>
                                {algorithms.map(algo => <option key={algo} value={algo}>{algo}</option>)}
                            </select>
                        </div>

                        {selectedAlgorithm === 'RR' && (
                            <div className="form-group">
                                <label htmlFor="quantum-input">Quantum Time Slice</label>
                                <input
                                    type="number"
                                    id="quantum-input"
                                    value={quantum}
                                    onChange={e => setQuantum(Number(e.target.value))}
                                    min="1"
                                />
                            </div>
                        )}

                        {/* We now pass the current state values to the handler */}
                        <button 
                            className="btn-run" 
                            onClick={() => runSimulation(selectedAlgorithm, processConfig, quantum)} 
                            disabled={isLoading || !selectedAlgorithm}>
                            {isLoading ? 'Simulating...' : 'Run Simulation'}
                        </button>
                    </div>
                    <div className="card">{/* ... la zone de texte est inchangée ... */}</div>
                </div>
                <div className="results-panel">{/* ... la zone de résultats est inchangée ... */}</div>
            </main>
        </div>
    );
}

export default App;
